graphql.query:
mutation createTransferRequest {
  createTransferRequest(input:
                            {
                                currency: "{{currencyValue}}", 
                                requestDetails:
                                    {
                                        id: "{{destinationUserIdValue}}",
                                        kashtag: "{{destinationKashtagValue}}",
                                        name: "{{destinationFullNameValue}}"
                                    },
                                    requestType: O2O,
                                    amount: {{amountRequestValue}},
                                    description: "Request Money of {{destinationKashtagValue}}"})
                            {
    requestID
  }
}

